// src/pages/ReportList.tsx

import React, { useEffect, useState, useMemo } from 'react';
import axios from '../utils/axiosInstance';
import { IReport, IBranch } from '../types';
import {
  Container,
  Typography,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Alert,
  IconButton,
  Tooltip,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
} from '@mui/material';
import { Visibility } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { LocalizationProvider, DatePicker } from '@mui/lab';
import AdapterDateFns from '@mui/lab/AdapterDateFns';

const ReportList: React.FC = () => {
  const [reports, setReports] = useState<IReport[]>([]);
  const [branches, setBranches] = useState<IBranch[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Search and Filter States
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [selectedBranch, setSelectedBranch] = useState<string>(''); // For branch filter
  const [startDate, setStartDate] = useState<Date | null>(null); // For date range filter
  const [endDate, setEndDate] = useState<Date | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [reportsResponse, branchesResponse] = await Promise.all([
          axios.get('/api/reports'),
          axios.get('/api/branches'), // Ensure this endpoint exists
        ]);
        setReports(reportsResponse.data.reports);
        setBranches(branchesResponse.data.branches);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load data.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Debugging: Log the reports to verify the structure
  useEffect(() => {
    console.log('Fetched Reports:', reports);
  }, [reports]);

  // Filtered Reports based on search and filters
  const filteredReports = useMemo(() => {
    return reports.filter((report) => {
      // Filter by selected branch
      const branchMatch = selectedBranch
        ? report.branchId?.name === selectedBranch
        : true;

      // Filter by search query (searching in branch name and notes)
      const searchMatch =
        (report.branchId?.name &&
          report.branchId.name.toLowerCase().includes(searchQuery.toLowerCase())) ||
        (report.notes &&
          report.notes.toLowerCase().includes(searchQuery.toLowerCase()));

      // Filter by date range
      const reportDate = new Date(report.dateSent);
      const startDateMatch = startDate ? reportDate >= startDate : true;
      const endDateMatch = endDate ? reportDate <= endDate : true;

      return branchMatch && searchMatch && startDateMatch && endDateMatch;
    });
  }, [reports, searchQuery, selectedBranch, startDate, endDate]);

  return (
    <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" align="center" gutterBottom>
        דוחות מלאי
      </Typography>

      <LocalizationProvider dateAdapter={AdapterDateFns}>
        <Box sx={{ mb: 3 }}>
          <Grid container spacing={2} alignItems="center">
            {/* Search Input */}
            <Grid item xs={12} sm={6} md={4}>
              <TextField
                label="חיפוש"
                variant="outlined"
                fullWidth
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="חפש לפי שם סניף או הערות"
              />
            </Grid>

            {/* Branch Filter */}
            <Grid item xs={12} sm={6} md={4}>
              <FormControl fullWidth variant="outlined">
                <InputLabel id="branch-filter-label">סניף</InputLabel>
                <Select
                  labelId="branch-filter-label"
                  label="סניף"
                  value={selectedBranch}
                  onChange={(e) => setSelectedBranch(e.target.value)}
                >
                  <MenuItem value="">
                    <em>כל הסניפים</em>
                  </MenuItem>
                  {branches.map((branch) => (
                    <MenuItem key={branch._id} value={branch.name}>
                      {branch.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>

            {/* Start Date Filter */}
            <Grid item xs={12} sm={6} md={2}>
              <DatePicker
                label="מתאריך"
                value={startDate}
                onChange={(newValue) => setStartDate(newValue)}
                renderInput={(params) => <TextField {...params} fullWidth />}
              />
            </Grid>

            {/* End Date Filter */}
            <Grid item xs={12} sm={6} md={2}>
              <DatePicker
                label="עד תאריך"
                value={endDate}
                onChange={(newValue) => setEndDate(newValue)}
                renderInput={(params) => <TextField {...params} fullWidth />}
              />
            </Grid>
          </Grid>
        </Box>
      </LocalizationProvider>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Alert severity="error">{error}</Alert>
      ) : filteredReports.length === 0 ? (
        <Alert severity="info">אין דוחות זמינים להצגה.</Alert>
      ) : (
        <TableContainer component={Paper}>
          <Table aria-label="reports table">
            <TableHead>
              <TableRow>
                <TableCell>
                  <strong>סניף</strong>
                </TableCell>
                <TableCell>
                  <strong>תאריך נשלח</strong>
                </TableCell>
                <TableCell>
                  <strong>הערות</strong>
                </TableCell>
                <TableCell align="center">
                  <strong>פעולות</strong>
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredReports.map((report) => (
                <TableRow key={report._id} hover>
                  <TableCell>{report.branchId?.name || 'N/A'}</TableCell>
                  <TableCell>
                    {new Date(report.dateSent).toLocaleDateString('he-IL', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit',
                    })}
                  </TableCell>
                  <TableCell>{report.notes || 'אין הערות'}</TableCell>
                  <TableCell align="center">
                    <Tooltip title="צפה בדוח">
                      <IconButton
                        component={Link}
                        to={`/reports/${report._id}`}
                        color="primary"
                      >
                        <Visibility />
                      </IconButton>
                    </Tooltip>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Container>
  );
};

export default ReportList;
