// src/App.tsx

import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';

import Navbar from './components/Navbar';
import AddItem from './pages/AddItem';
import EditItem from './pages/EditItem';
import ItemList from './pages/ItemList';
import Login from './pages/Login'; // New login page
import CreateBranch from './pages/CreateBranch'; // New create branch page
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import Layout from './components/Layout'; // New Layout component
import NavigateToFirstBranch from './components/NavigateToFirstBranch'; // New component for redirection

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <AuthProvider>
        <Router>
          <Navbar />
          <Routes>
            {/* Redirect root to the first branch or to create a branch if none exist */}
            <Route path="/" element={<NavigateToFirstBranch />} />

            {/* Layout route for branch-specific pages */}
            <Route path="/branch/:branchId" element={<Layout />}>
              <Route path="items" element={<ItemList />} />
              <Route
                path="add"
                element={
                  <ProtectedRoute requiredRole="admin">
                    <AddItem />
                  </ProtectedRoute>
                }
              />
              <Route
                path="edit/:id"
                element={
                  <ProtectedRoute requiredRole="admin">
                    <EditItem />
                  </ProtectedRoute>
                }
              />
              <Route
                path="create-branch"
                element={
                  <ProtectedRoute requiredRole="admin">
                    <CreateBranch />
                  </ProtectedRoute>
                }
              />
            </Route>

            {/* Login Route */}
            <Route path="/login" element={<Login />} />

            {/* Fallback Route for 404 */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </Router>
      </AuthProvider>
    </ThemeProvider>
  );
}

// Simple NotFound component
const NotFound: React.FC = () => (
  <div style={{ textAlign: 'center', marginTop: '50px' }}>
    <h2>404 - Page Not Found</h2>
  </div>
);

export default App;
