// src/pages/ItemList.tsx

import React, { useState, useEffect, useContext } from "react";
import axios from "../utils/axiosInstance";
import { IItem } from "../types";
import { Link } from "react-router-dom";
import { Edit, Delete } from "@mui/icons-material";
import { useParams } from "react-router-dom";

// Import MUI components
import {
  Container,
  Typography,
  Table,
  TableContainer,
  Card,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Button,
  IconButton,
  Paper,
  Grid,
  useMediaQuery,
  useTheme,
} from "@mui/material";

// Import AuthContext
import { AuthContext } from "../context/AuthContext";

const ItemList: React.FC = () => {
  const { role } = useContext(AuthContext); // Access the role from context
  const [items, setItems] = useState<IItem[]>([]);
  const { branchId } = useParams<{ branchId: string }>(); // Get branchId from the URL

  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    if (!branchId) {
      console.error("No branchId provided in URL parameters.");
      return;
    }

    console.log(`Fetching items for branchId: ${branchId}`);

    axios
      .get<IItem[]>(`/api/${branchId}/items`)
      .then((response) => {
        const filteredItems = response.data.filter(
          (item) => item.branch === branchId
        );
        setItems(filteredItems);
        console.log("Fetched Items:", filteredItems);
      })
      .catch((error) => console.error("Error fetching items:", error));
  }, [branchId]); // Added branchId to dependency array

  const deleteItem = (id: string) => {
    if (window.confirm("Are you sure you want to delete this item?")) {
      axios
        .delete(`/api/${branchId}/items/${id}`)
        .then(() => {
          setItems(items.filter((item) => item._id !== id));
          console.log(`Deleted item with id: ${id}`);
        })
        .catch((error) => console.error("Error deleting item:", error));
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography
        variant={isSmallScreen ? "h5" : "h4"}
        align="center"
        gutterBottom
      >
        מוצרי מלאי
      </Typography>

      {/* Buttons Section */}
      <Grid
        container
        spacing={2}
        justifyContent={isSmallScreen ? "center" : "flex-end"}
        sx={{ mb: 2 }}
      >
        {role === "admin" && branchId && (
          <Grid item xs={12} sm="auto">
            <Button
              variant="contained"
              color="primary"
              component={Link}
              to={`/branch/${branchId}/add`} // Navigate to the correct branch-specific add item page
              fullWidth={isSmallScreen}
            >
              + הוסף מוצר חדש
            </Button>
          </Grid>
        )}
        {role === "employee" && branchId && (
          <Grid item xs={12} sm="auto">
            <Button
              variant="contained"
              color="secondary"
              component={Link}
              to={`/branch/${branchId}/sendreport`} // Navigate to the correct branch-specific send report page
              fullWidth={isSmallScreen}
            >
              שלח דוח מלאי
            </Button>
          </Grid>
        )}
      </Grid>

      {/* Table Section */}
      <TableContainer component={Paper} sx={{ overflowX: "auto" }}>
        <Card
          sx={{
            backgroundColor: "rgba(255, 255, 255, 0.95)", // Slightly more opaque for better readability
            borderRadius: 2,
            boxShadow: 3,
            padding: { xs: 1, md: 2 },
          }}
        >
          <Table
            aria-label="item table"
            sx={{
              minWidth: 650,
            }}
          >
            <TableHead>
              <TableRow>
                <TableCell>
                  <strong>שם</strong>
                </TableCell>
                {!isSmallScreen && (
                  <>
                    <TableCell>
                      <strong>תיאור</strong>
                    </TableCell>
                    <TableCell>
                      <strong>מחיר ($)</strong>
                    </TableCell>
                  </>
                )}
                <TableCell>
                  <strong>כמות</strong>
                </TableCell>
                {!isSmallScreen && (
                  <TableCell>
                    <strong>תאריך הוספה</strong>
                  </TableCell>
                )}
                {role === "admin" && (
                  <TableCell align="center">
                    <strong>פעולות</strong>
                  </TableCell>
                )}
              </TableRow>
            </TableHead>
            <TableBody>
              {items.length === 0 ? (
                <TableRow>
                  <TableCell
                    colSpan={
                      role === "admin"
                        ? isSmallScreen
                          ? 3
                          : 5
                        : isSmallScreen
                        ? 2
                        : 4
                    }
                    align="center"
                  >
                    אין מוצרים זמינים.
                  </TableCell>
                </TableRow>
              ) : (
                items.map((item) => (
                  <TableRow key={item._id} hover>
                    <TableCell>{item.name}</TableCell>
                    {!isSmallScreen && (
                      <>
                        <TableCell>{item.description || "N/A"}</TableCell>
                        <TableCell>
                          {item.price ? item.price.toFixed(2) : "N/A"}
                        </TableCell>
                      </>
                    )}
                    <TableCell>{item.quantity}</TableCell>
                    {!isSmallScreen && (
                      <TableCell>
                        {item.dateAdded
                          ? new Date(item.dateAdded).toLocaleDateString()
                          : "N/A"}
                      </TableCell>
                    )}
                    {role === "admin" && (
                      <TableCell align="center">
                        {/* Conditionally render Edit and Delete buttons */}
                        <>
                          <IconButton
                            component={Link}
                            to={`/branch/${branchId}/edit/${item._id}`}
                            color="primary"
                            size={isSmallScreen ? "small" : "medium"}
                          >
                            <Edit />
                          </IconButton>
                          <IconButton
                            onClick={() => deleteItem(item._id!)}
                            color="secondary"
                            size={isSmallScreen ? "small" : "medium"}
                          >
                            <Delete />
                          </IconButton>
                        </>
                      </TableCell>
                    )}
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </Card>
      </TableContainer>
    </Container>
  );
};

export default ItemList;
