// src/pages/ItemList.tsx

import React, { useState, useEffect, useContext } from "react";
import axios from "../utils/axiosInstance";
import { IItem } from "../types";
import { Link } from "react-router-dom";
import { Edit, Delete } from "@mui/icons-material";
import { useParams } from "react-router-dom";

// Import MUI components
import {
  Container,
  Typography,
  Table,
  TableContainer,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Button,
  IconButton,
  Paper,
  Grid,
  useMediaQuery,
  useTheme,
} from "@mui/material";

// Import AuthContext
import { AuthContext } from "../context/AuthContext";

const ItemList: React.FC = () => {
  const { role } = useContext(AuthContext); // Access the role from context
  const [items, setItems] = useState<IItem[]>([]);
  const { branchId } = useParams<{ branchId: string }>(); // Get branchId from the URL

  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  useEffect(() => {
    if (!branchId) {
      console.error("No branchId provided in URL parameters.");
      return;
    }

    console.log(`Fetching items for branchId: ${branchId}`);

    axios
      .get<IItem[]>(`/api/${branchId}/items`)
      .then((response) => {
        const filteredItems = response.data.filter(
          (item) => item.branch === branchId
        );
        setItems(filteredItems);
        console.log("Fetched Items:", filteredItems);
      })
      .catch((error) => console.error("Error fetching items:", error));
  }, [branchId]); // Added branchId to dependency array

  const deleteItem = (id: string) => {
    if (window.confirm(`האם אתה בטוח?`)) {
      axios
        .delete(`/api/${branchId}/items/${id}`)
        .then(() => {
          setItems(items.filter((item) => item._id !== id));
          console.log(`Deleted item with id: ${id}`);
        })
        .catch((error) => console.error("Error deleting item:", error));
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography
        variant={isSmallScreen ? "h5" : "h4"}
        align="center"
        gutterBottom
      >
        מוצרי מלאי
      </Typography>

      {/* Buttons Section */}
      <Grid
        container
        spacing={2}
        justifyContent={isSmallScreen ? "center" : "flex-end"}
        sx={{ mb: 2 }}
      >
        {role === "admin" && branchId && (
          <Grid item xs={12} sm="auto">
            <Button
              variant="contained"
              component={Link}
              to={`/branch/${branchId}/add`} // Navigate to the correct branch-specific add item page
              fullWidth={isSmallScreen}
              sx={{
                backgroundColor: "#333333", // Dark background color
                color: "#ffffff", // White text color
                "&:hover": {
                  backgroundColor: "#555555", // Slightly lighter dark on hover
                },
              }}
            >
              + הוסף מוצר חדש
            </Button>
          </Grid>
        )}
        {role === "employee" && branchId && (
          <Grid item xs={12} sm="auto">
            <Button
              variant="contained"
              component={Link}
              sx={{backgroundColor: "#333333", // Dark background color
                color: "#ffffff", // White text color
                "&:hover": {
                  backgroundColor: "#555555", // Slightly lighter dark on hover
                } }}
              to={`/branch/${branchId}/sendreport`} // Navigate to the correct branch-specific send report page
              fullWidth={isSmallScreen}
            >
              שלח דוח מלאי
            </Button>
          </Grid>
        )}
      </Grid>

      {/* Table Section */}
      <TableContainer component={Paper} sx={{ overflowX: "auto" }}>
        <Table
          aria-label="item table"
          sx={{
            minWidth: 650,
            "& th, & td": {
              padding: isSmallScreen ? theme.spacing(1) : theme.spacing(2),
              fontSize: isSmallScreen ? "0.875rem" : "1rem",
            },
          }}
        >
          <TableHead>
            <TableRow>
              <TableCell>
                <strong>שם</strong>
              </TableCell>
              <TableCell>
                <strong>תיאור</strong>
              </TableCell>
              <TableCell>
                <strong>מחיר ($)</strong>
              </TableCell>
              <TableCell>
                <strong>כמות</strong>
              </TableCell>
              <TableCell>
                <strong>תאריך הוספה</strong>
              </TableCell>
              {role === "admin" && (
                <TableCell align="center">
                  <strong>פעולות</strong>
                </TableCell>
              )}
            </TableRow>
          </TableHead>
          <TableBody>
            {items.length === 0 ? (
              <TableRow>
                <TableCell colSpan={role === "admin" ? 6 : 5} align="center">
                  אין מוצרים זמינים.
                </TableCell>
              </TableRow>
            ) : (
              items.map((item) => (
                <TableRow key={item._id} hover>
                  <TableCell>{item.name}</TableCell>
                  <TableCell>{item.description || "N/A"}</TableCell>
                  <TableCell>
                    {item.price ? item.price.toFixed(2) : "N/A"}
                  </TableCell>
                  <TableCell>{item.quantity}</TableCell>
                  <TableCell>
                    {item.dateAdded
                      ? new Date(item.dateAdded).toLocaleDateString()
                      : "N/A"}
                  </TableCell>
                  {role === "admin" && (
                    <TableCell align="center">
                      {/* Conditionally render Edit and Delete buttons */}
                      <>
                        {/* Edit Button */}
                        <IconButton
                          component={Link}
                          to={`/branch/${branchId}/edit/${item._id}`}
                          sx={{
                            backgroundColor: "#FFC107", // Amber (yellow) color
                            color: "#ffffff",
                            mr: 1, // Margin right to add spacing between buttons
                            "&:hover": {
                              backgroundColor: "#FFA000", // Darker amber on hover
                            },
                          }}
                          size={isSmallScreen ? "small" : "medium"}
                        >
                          <Edit />
                        </IconButton>

                        {/* Delete Button */}
                        <IconButton
                          onClick={() => deleteItem(item._id!)}
                          size={isSmallScreen ? "small" : "medium"}
                          sx={{
                            backgroundColor: "#F44336", // Red color
                            color: "#ffffff",
                            "&:hover": {
                              backgroundColor: "#D32F2F", // Darker red on hover
                            },
                          }}
                        >
                          <Delete />
                        </IconButton>
                      </>
                    </TableCell>
                  )}
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default ItemList;
