// src/components/Navbar.tsx

import React, { useContext } from "react";
import { Link } from "react-router-dom";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  IconButton,
  Grid,
  Tooltip,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import { AuthContext } from "../context/AuthContext";
import PhoneAndroidIcon from "@mui/icons-material/PhoneAndroid"; // Example icon, replace with your logo if available
import MenuIcon from "@mui/icons-material/Menu"; // For hamburger menu
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const Navbar: React.FC = () => {
  const { token, role, logout } = useContext(AuthContext);
  const navigate = useNavigate(); // Initialize the navigate function
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  // State for Drawer (Hamburger Menu)
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleLogout = async () => {
    try {
      await logout(); // Perform the logout action
      navigate("/"); // Redirect to the homepage
    } catch (error) {
      console.error("Logout failed:", error);
      // Optionally, handle the error (e.g., display a notification)
    }
  };

  // Toggle Drawer Function
  const toggleDrawer = (open: boolean) => () => {
    setDrawerOpen(open);
  };

  // Render List Items for Drawer
  const drawerList = (
    <Box
      sx={{ width: 250 }}
      role="presentation"
      onClick={toggleDrawer(false)}
      onKeyDown={toggleDrawer(false)}
    >
      <List>
        {token ? (
          <>
            <ListItem button component={Link} to="/">
              <ListItemText primary="דף בית" />
            </ListItem>
            {role === "admin" && (
              <ListItem button component={Link} to="/create-branch">
                <ListItemText primary="צור סניף" />
              </ListItem>
            )}
            <ListItem button onClick={handleLogout}>
              <ListItemText primary="התנתק" />
            </ListItem>
          </>
        ) : (
          <ListItem button component={Link} to="/login">
            <ListItemText primary="התחבר" />
          </ListItem>
        )}
      </List>
    </Box>
  );

  return (
    <AppBar position="static" color="default" elevation={1}>
      <Toolbar>
        {/* Logo and Title */}
        <Box sx={{ display: "flex", alignItems: "center", mr: 2 }}>
          {/* Replace PhoneAndroidIcon with your logo if available */}
          <IconButton
            edge="start"
            color="inherit"
            aria-label="logo"
            component={Link}
            to="/"
            sx={{ mr: 1 }}
          >
            <PhoneAndroidIcon />
          </IconButton>
          <Typography
            variant="h6"
            component={Link}
            to="/"
            sx={{
              textDecoration: "none",
              color: "inherit",
              fontWeight: "bold",
            }}
          >
            ניהול מלאי
          </Typography>
        </Box>

        {/* Spacer */}
        <Box sx={{ flexGrow: 1 }} />

        {/* Hamburger Menu Icon for Small Screens */}
        {isSmallScreen ? (
          <>
            <IconButton
              edge="end"
              color="inherit"
              aria-label="menu"
              onClick={toggleDrawer(true)}
            >
              <MenuIcon />
            </IconButton>
            <Drawer
              anchor="right"
              open={drawerOpen}
              onClose={toggleDrawer(false)}
            >
              {drawerList}
            </Drawer>
          </>
        ) : (
          /* Navigation Buttons for Larger Screens */
          <Grid container spacing={1}>
            {/* Home Button */}
            <Grid item>
              <Tooltip title="דף הבית">
                <Button
                  color="inherit"
                  component={Link}
                  to="/"
                  fullWidth
                  sx={{
                    textTransform: "none",
                    fontSize: { xs: "0.875rem", sm: "1rem" }, // Responsive font size
                    "&:hover": {
                      backgroundColor: "rgba(0, 0, 0, 0.04)",
                    },
                  }}
                >
                  דף בית
                </Button>
              </Tooltip>
            </Grid>

            {/* Create Branch Button (Visible Only to Admins) */}
            {role === "admin" && (
              <Grid item>
                <Tooltip title="צור סניף חדש">
                  <Button
                    color="inherit"
                    component={Link}
                    to="/create-branch"
                    fullWidth
                    sx={{
                      textTransform: "none",
                      fontSize: { xs: "0.875rem", sm: "1rem" }, // Responsive font size
                      "&:hover": {
                        backgroundColor: "rgba(0, 0, 0, 0.04)",
                      },
                    }}
                  >
                    צור סניף
                  </Button>
                </Tooltip>
              </Grid>
            )}

            {/* Logout Button */}
            <Grid item>
              <Tooltip title="התנתק">
                <Button
                  color="inherit"
                  onClick={handleLogout}
                  fullWidth
                  sx={{
                    textTransform: "none",
                    fontSize: { xs: "0.875rem", sm: "1rem" }, // Responsive font size
                    "&:hover": {
                      backgroundColor: "rgba(0, 0, 0, 0.04)",
                    },
                  }}
                >
                  התנתק
                </Button>
              </Tooltip>
            </Grid>
          </Grid>
        )}

        {/* Login Button for Unauthenticated Users */}
        {!token && !isSmallScreen && (
          <Tooltip title="התחבר">
            <Button
              color="inherit"
              component={Link}
              to="/login"
              fullWidth
              sx={{
                textTransform: "none",
                fontSize: { xs: "0.875rem", sm: "1rem" }, // Responsive font size
                "&:hover": {
                  backgroundColor: "rgba(0, 0, 0, 0.04)",
                },
              }}
            >
              התחבר
            </Button>
          </Tooltip>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
