// src/pages/HomePage.tsx

import React, { useContext } from "react";
import { Link as RouterLink } from "react-router-dom";
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  useTheme,
  useMediaQuery,
} from "@mui/material";
import { AuthContext } from '../context/AuthContext';

const HomePage: React.FC = () => {
  const { role } = useContext(AuthContext); // Access user and role from context
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('md'));

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: `url('https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=1950&q=80')`, // Replace with your chosen background image
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundPosition: "center",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: 4,
      }}
    >
      <Container maxWidth="lg">
        <Card
          sx={{
            backgroundColor: "rgba(255, 255, 255, 0.9)",
            borderRadius: 4,
            boxShadow: 6,
            padding: { xs: 2, md: 4 },
          }}
        >
          <Grid container spacing={4} alignItems="center">
            {/* Content Section */}
            <Grid item xs={12} md={6}>
              <CardContent>
                <Typography variant="h3" component="div" gutterBottom>
                  Elevate Your Mobile Experience
                </Typography>
                <Typography variant="h6" color="text.secondary" paragraph>
                  Discover our wide range of premium cell phone accessories designed to enhance your device's functionality and style. From durable cases to fast chargers, we have everything you need to keep your phone protected and performing at its best.
                </Typography>
                <Box sx={{ mt: 4 }}>
                  {role ? (
                    <>
                      <Button
                        variant="contained"
                        color="primary"
                        component={RouterLink}
                        to="/branch/select" // Assuming you have a branch selection page
                        sx={{ mr: 2, mb: { xs: 2, sm: 0 } }}
                      >
                        View Inventory
                      </Button>
                      {role === 'admin' && (
                        <Button
                          variant="outlined"
                          color="primary"
                          component={RouterLink}
                          to="/create-branch"
                        >
                          Manage Branches
                        </Button>
                      )}
                    </>
                  ) : (
                    <>
                      <Button
                        variant="contained"
                        color="primary"
                        component={RouterLink}
                        to="/login"
                        sx={{ mr: 2, mb: { xs: 2, sm: 0 } }}
                      >
                        Login
                      </Button>
                      <Button
                        variant="outlined"
                        color="primary"
                        component={RouterLink}
                        to="/register" // Assuming you have a registration page
                      >
                        Register
                      </Button>
                    </>
                  )}
                </Box>
              </CardContent>
            </Grid>

            {/* Image Section */}
            <Grid item xs={12} md={6}>
              <CardMedia
                component="img"
                height="100%"
                image="https://images.unsplash.com/photo-1581235720701-34f3dbfe1b20?auto=format&fit=crop&w=800&q=80" // Replace with your chosen product image
                alt="Phone Accessories"
                sx={{
                  borderRadius: 2,
                  boxShadow: 3,
                  width: isSmallScreen ? '100%' : '80%',
                  margin: '0 auto',
                }}
              />
            </Grid>
          </Grid>
        </Card>
      </Container>
    </Box>
  );
};

export default HomePage;
