// src/pages/HomePage.tsx

import React, { useContext } from "react";
import { Link as RouterLink } from "react-router-dom";
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
} from "@mui/material";
import { AuthContext } from '../context/AuthContext';

const HomePage: React.FC = () => {
  const { role } = useContext(AuthContext); // Access user and role from context

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: `url('https://images.unsplash.com/photo-1518779578993-ec3579fee39f?auto=format&fit=crop&w=1950&q=80')`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        backgroundPosition: "center",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: 4,
      }}
    >
      <Container maxWidth="md">
        <Card
          sx={{
            backgroundColor: "rgba(255, 255, 255, 0.85)",
            borderRadius: 3,
            boxShadow: 3,
          }}
        >
          <Grid container spacing={2}>
            {/* Optional Image Section */}
            <Grid item xs={12} md={6}>
              <CardMedia
                component="img"
                height="100%"
                image="https://images.unsplash.com/photo-1581091012184-7e2916fb77c1?auto=format&fit=crop&w=800&q=80"
                alt="Inventory Management"
                sx={{ borderTopLeftRadius: 3, borderBottomLeftRadius: 3 }}
              />
            </Grid>

            {/* Content Section */}
            <Grid item xs={12} md={6}>
              <CardContent>
                <Typography variant="h3" component="div" gutterBottom>
                  Welcome to Inventory Manager
                </Typography>
                <Typography variant="h6" color="text.secondary" paragraph>
                  Efficiently manage your inventory, track stock levels, and streamline your operations with our intuitive platform.
                </Typography>
                <Box sx={{ mt: 4 }}>
                  {role ? (
                    <>
                      <Button
                        variant="contained"
                        color="primary"
                        component={RouterLink}
                        to="/branch/select" // Assuming you have a branch selection page
                        sx={{ mr: 2, mb: { xs: 2, sm: 0 } }}
                      >
                        View Inventory
                      </Button>
                      {role === 'admin' && (
                        <Button
                          variant="outlined"
                          color="primary"
                          component={RouterLink}
                          to="/create-branch"
                        >
                          Manage Branches
                        </Button>
                      )}
                    </>
                  ) : (
                    <>
                      <Button
                        variant="contained"
                        color="primary"
                        component={RouterLink}
                        to="/login"
                        sx={{ mr: 2, mb: { xs: 2, sm: 0 } }}
                      >
                        Login
                      </Button>
                      <Button
                        variant="outlined"
                        color="primary"
                        component={RouterLink}
                        to="/register" // Assuming you have a registration page
                      >
                        Register
                      </Button>
                    </>
                  )}
                </Box>
              </CardContent>
            </Grid>
          </Grid>
        </Card>
      </Container>
    </Box>
  );
};

export default HomePage;
